import{_ as e,c as i,o as a,a4 as t}from"./chunks/framework.ttppV29E.js";const m=JSON.parse('{"title":"Releases","description":"","frontmatter":{"type":"page"},"headers":[],"relativePath":"releases.md","filePath":"releases.md","lastUpdated":1771516590000}'),r={name:"releases.md"},l=t('<h1 id="releases" tabindex="-1">Releases <a class="header-anchor" href="#releases" aria-label="Permalink to &quot;Releases&quot;">​</a></h1><h2 id="_2-37-2-bug-fixes" tabindex="-1">2.37.2 - Bug Fixes <a class="header-anchor" href="#_2-37-2-bug-fixes" aria-label="Permalink to &quot;2.37.2 - Bug Fixes&quot;">​</a></h2><p><strong>Feb 3, 2026</strong></p><ul><li>Add tenant-level deny list for task expiry via in UI</li></ul><h2 id="_2-37-1-bug-fixes" tabindex="-1">2.37.1 - Bug Fixes <a class="header-anchor" href="#_2-37-1-bug-fixes" aria-label="Permalink to &quot;2.37.1 - Bug Fixes&quot;">​</a></h2><p><strong>Feb 3, 2026</strong></p><ul><li>Allow moving form tasks from In Progress -&gt; Expired through UI</li><li>Improve validation of workflow definitions on save/import</li><li>Fix default expiry date when enabling automatic expiry for the first time</li><li>Bug fixes</li></ul><h2 id="_2-37-0" tabindex="-1">2.37.0 <a class="header-anchor" href="#_2-37-0" aria-label="Permalink to &quot;2.37.0&quot;">​</a></h2><p><strong>Jan 13, 2026</strong></p><ul><li>Increase security of management login (BFF)</li><li>Add &quot;custom&quot; base option for due/expiry dates</li><li>Rename &quot;now&quot; to &quot;creation date&quot; in due/expiry date base option</li><li>Display form task -&gt; custom event relationships on diagram</li><li>Update icons</li><li>Improve look and feel of relationship diagram</li></ul><h2 id="_2-36-0-automatic-task-expiry" tabindex="-1">2.36.0 - Automatic Task Expiry <a class="header-anchor" href="#_2-36-0-automatic-task-expiry" aria-label="Permalink to &quot;2.36.0 - Automatic Task Expiry&quot;">​</a></h2><p><strong>Dec 15, 2025</strong></p><ul><li><a href="/docs/tasks/automatic-expiry.html">Add automatic task expiry</a></li><li>Add <code>Expired</code> status, event and triggers</li><li>Allow debug to be changed for git workflows</li><li>Support mutliple email recipients using a semicolon separated list for tasks and alerts</li><li>Add login splash screen</li></ul><h2 id="_2-35-2" tabindex="-1">2.35.2 <a class="header-anchor" href="#_2-35-2" aria-label="Permalink to &quot;2.35.2&quot;">​</a></h2><p><strong>Nov 21, 2025</strong></p><ul><li>Use SES for alert emails</li></ul><h2 id="_2-35-1" tabindex="-1">2.35.1 <a class="header-anchor" href="#_2-35-1" aria-label="Permalink to &quot;2.35.1&quot;">​</a></h2><p><strong>Sep 24, 2025</strong></p><ul><li>Fix login for non aireinnovate.com URLs</li></ul><h2 id="_2-35-0-reliability-improvements" tabindex="-1">2.35.0 - Reliability Improvements <a class="header-anchor" href="#_2-35-0-reliability-improvements" aria-label="Permalink to &quot;2.35.0 - Reliability Improvements&quot;">​</a></h2><p><strong>Sep 22, 2025</strong></p><ul><li>Improve reliability of graphQL tasks API &amp; event handling</li><li>Add API call to publish multiple events</li><li>Add more information to workflow logs</li><li>Allow mutations to be triggered by their own task</li></ul><h2 id="_2-34-0-form-task-improvements-docs-bot" tabindex="-1">2.34.0 - Form Task Improvements &amp; Docs Bot <a class="header-anchor" href="#_2-34-0-form-task-improvements-docs-bot" aria-label="Permalink to &quot;2.34.0 - Form Task Improvements &amp; Docs Bot&quot;">​</a></h2><p><strong>Aug 14, 2025</strong></p><ul><li><a href="/docs/tasks/form.html#complete-task-on-form-submission">Add ability to automatically update form task when form is updated</a></li><li><a href="/docs/events/aireforms.html">Add triggers for form events</a></li><li>Add Docs Bot to provide help with configuration</li></ul><h2 id="_2-33-2-fix-for-new-tenants" tabindex="-1">2.33.2 - Fix for New Tenants <a class="header-anchor" href="#_2-33-2-fix-for-new-tenants" aria-label="Permalink to &quot;2.33.2 - Fix for New Tenants&quot;">​</a></h2><p><strong>Jun 27, 2025</strong></p><ul><li>Fix error when adding new tenants</li><li>Improve performance of counting in graphql queries</li></ul><h2 id="_2-33-1-audit-fix" tabindex="-1">2.33.1 - Audit Fix <a class="header-anchor" href="#_2-33-1-audit-fix" aria-label="Permalink to &quot;2.33.1 - Audit Fix&quot;">​</a></h2><p><strong>Jun 3, 2025</strong></p><ul><li>Fix audit failures by reducing size of logs</li></ul><h2 id="_2-33-0" tabindex="-1">2.33.0 <a class="header-anchor" href="#_2-33-0" aria-label="Permalink to &quot;2.33.0&quot;">​</a></h2><p><strong>May 29, 2025</strong></p><ul><li>Add AireAuth event trigger</li><li>Improve GraphQL API performance</li><li>Fix bug when creating or updating repository fails</li></ul><h2 id="_2-32-0-form-task-enhancements" tabindex="-1">2.32.0 - Form Task Enhancements <a class="header-anchor" href="#_2-32-0-form-task-enhancements" aria-label="Permalink to &quot;2.32.0 - Form Task Enhancements&quot;">​</a></h2><p><strong>May 14, 2025</strong></p><ul><li>Ability to link form tasks to existing forms</li><li>Bug fixes</li></ul><h2 id="_2-31-0-tenant-specific-smtp" tabindex="-1">2.31.0 - Tenant Specific SMTP <a class="header-anchor" href="#_2-31-0-tenant-specific-smtp" aria-label="Permalink to &quot;2.31.0 - Tenant Specific SMTP&quot;">​</a></h2><p><strong>Apr 3, 2025</strong></p><ul><li>Add tenant-specific SMTP configuration</li><li>Fix metrics task paging</li><li>Increase event maximum XML depth</li></ul><h2 id="_2-30-0-owner-type-and-form-task-improvements" tabindex="-1">2.30.0 - Owner Type and Form task improvements <a class="header-anchor" href="#_2-30-0-owner-type-and-form-task-improvements" aria-label="Permalink to &quot;2.30.0 - Owner Type and Form task improvements&quot;">​</a></h2><p><strong>Mar 3, 2025</strong></p><ul><li>Added in Owner Type option for tasks to describe the type of the Owner</li><li>When set, Owner and Owner Type are used for &#39;OnBehalfOf&#39; settings for the Form task when creating the form</li><li>Due dates can now be set for Form task</li><li>Multiple task type filters can be selected on task view - Form tasks shown by default alongside Manual tasks</li><li>Bug fixes and general improvments</li></ul><h2 id="_2-29-0-restrictions-form-task-tweaks" tabindex="-1">2.29.0 - Restrictions &amp; Form Task Tweaks <a class="header-anchor" href="#_2-29-0-restrictions-form-task-tweaks" aria-label="Permalink to &quot;2.29.0 - Restrictions &amp; Form Task Tweaks&quot;">​</a></h2><p><strong>Feb 11, 2025</strong></p><ul><li>Add support for Identity resource restrictions</li><li>Bug fixes</li></ul><h2 id="_2-28-1-bug-fixes" tabindex="-1">2.28.1 - Bug Fixes <a class="header-anchor" href="#_2-28-1-bug-fixes" aria-label="Permalink to &quot;2.28.1 - Bug Fixes&quot;">​</a></h2><p><strong>Jan 9, 2025</strong></p><ul><li>Minor bug fixes</li><li>Improved validation of automated tasks</li></ul><h2 id="_2-28-0-form-task" tabindex="-1">2.28.0 - Form Task <a class="header-anchor" href="#_2-28-0-form-task" aria-label="Permalink to &quot;2.28.0 - Form Task&quot;">​</a></h2><p><strong>Dec 12, 2024</strong></p><ul><li>Add new Form task type</li><li>Bug fixes</li></ul><h2 id="_2-27-0-failed-task-retries-metrics-improvements-and-publish-event-task-type-added" tabindex="-1">2.27.0 - Failed task retries, Metrics improvements, and Publish Event task type added <a class="header-anchor" href="#_2-27-0-failed-task-retries-metrics-improvements-and-publish-event-task-type-added" aria-label="Permalink to &quot;2.27.0 - Failed task retries, Metrics improvements, and Publish Event task type added&quot;">​</a></h2><p><strong>Oct 30, 2024</strong></p><ul><li>Automated tasks allowed to be retried after failure</li><li>Added the Publish Event task type</li><li>Renamed Operational Overview to Metrics with improvements</li><li>AireFlow now listens out for AireFrame Structural Entity changes</li><li>General Improvements</li><li>Bug fixes</li></ul><h2 id="_2-26-0-metrics-and-aireaudit-integration" tabindex="-1">2.26.0 - Metrics and AireAudit integration <a class="header-anchor" href="#_2-26-0-metrics-and-aireaudit-integration" aria-label="Permalink to &quot;2.26.0 - Metrics and AireAudit integration&quot;">​</a></h2><p><strong>Sep 17, 2024</strong></p><ul><li>Added Metrics page to see workflow tasks at a glance</li><li>AireFlow now integrated with AireAudit for audit logs</li><li>General Improvements</li><li>Bug fixes</li></ul><h2 id="_2-25-1-task-assignee-type" tabindex="-1">2.25.1 - Task Assignee Type <a class="header-anchor" href="#_2-25-1-task-assignee-type" aria-label="Permalink to &quot;2.25.1 - Task Assignee Type&quot;">​</a></h2><p><strong>Aug 08, 2024</strong></p><ul><li>Assignee Type added to task definition</li><li>General Improvements</li><li>Bug fixes</li></ul><h2 id="_2-25-0-event-bus" tabindex="-1">2.25.0 - Event Bus <a class="header-anchor" href="#_2-25-0-event-bus" aria-label="Permalink to &quot;2.25.0 - Event Bus&quot;">​</a></h2><p><strong>Jul 09, 2024</strong></p><ul><li>Allow tasks to be triggered by AireFrame events</li><li>Automatically create tenants when added to Identity</li><li>Restrict tenant APIs to root tenant</li><li>Bug fixes</li></ul><h2 id="_2-24-2-internal-networking" tabindex="-1">2.24.2 - Internal Networking <a class="header-anchor" href="#_2-24-2-internal-networking" aria-label="Permalink to &quot;2.24.2 - Internal Networking&quot;">​</a></h2><p><strong>Jul 01, 2024</strong></p><ul><li>Enhancements to internal networking</li></ul><h2 id="_2-24-1-bug-fixes" tabindex="-1">2.24.1 - Bug Fixes <a class="header-anchor" href="#_2-24-1-bug-fixes" aria-label="Permalink to &quot;2.24.1 - Bug Fixes&quot;">​</a></h2><p><strong>Jun 26, 2024</strong></p><ul><li>General security fixes</li><li>Relationship diagram improvements</li><li>Reliability improvements</li></ul><h2 id="_2-24-0-workflow-log-alerts" tabindex="-1">2.24.0 - Workflow Log Alerts <a class="header-anchor" href="#_2-24-0-workflow-log-alerts" aria-label="Permalink to &quot;2.24.0 - Workflow Log Alerts&quot;">​</a></h2><p><strong>Jun 13, 2024</strong></p><ul><li>Allow Workflow log alerts to be configured for either email or an AireGlu endpoint</li><li>General security fixes</li><li>General improvements</li></ul><h2 id="_2-23-1-security-fixes" tabindex="-1">2.23.1 - Security Fixes <a class="header-anchor" href="#_2-23-1-security-fixes" aria-label="Permalink to &quot;2.23.1 - Security Fixes&quot;">​</a></h2><p><strong>Apr 25, 2024</strong></p><ul><li>Improve security of repository syncing</li></ul><h2 id="_2-23-0-event-handling-improvements-task-reordering" tabindex="-1">2.23.0 - Event Handling Improvements &amp; Task Reordering <a class="header-anchor" href="#_2-23-0-event-handling-improvements-task-reordering" aria-label="Permalink to &quot;2.23.0 - Event Handling Improvements &amp; Task Reordering&quot;">​</a></h2><p><strong>Mar 22, 2024</strong></p><ul><li>Allow reordering tasks in the workflow definition builder</li><li>Reduce delays to event handling due to locking</li><li>Improve clinical safety when removing task deinfitions</li><li>Package updates for security and reliability</li></ul><h2 id="_2-22-0-airevault-secrets-integration" tabindex="-1">2.22.0 - AireVault Secrets Integration <a class="header-anchor" href="#_2-22-0-airevault-secrets-integration" aria-label="Permalink to &quot;2.22.0 - AireVault Secrets Integration&quot;">​</a></h2><p><strong>Nov 15, 2023</strong></p><ul><li>AireVault secrets now integrated within AireFlow</li><li>Vault Secret Key now required for private repositories</li></ul><h2 id="_2-21-0-event-definition-groups-exclusive-tasks" tabindex="-1">2.21.0 - Event Definition Groups &amp; Exclusive Tasks <a class="header-anchor" href="#_2-21-0-event-definition-groups-exclusive-tasks" aria-label="Permalink to &quot;2.21.0 - Event Definition Groups &amp; Exclusive Tasks&quot;">​</a></h2><p><strong>Oct 18, 2023</strong></p><ul><li>Add event definition groups</li><li>Add a setting to mark a task as Exclusive</li><li>Event log search enhancements</li></ul><h2 id="_2-20-1-bug-fixes" tabindex="-1">2.20.1 - Bug Fixes <a class="header-anchor" href="#_2-20-1-bug-fixes" aria-label="Permalink to &quot;2.20.1 - Bug Fixes&quot;">​</a></h2><p><strong>Aug 14, 2023</strong></p><ul><li>Set default due expression when adding new task to workflow definition</li><li>Improve behaviour when setting Rows Per Page to &#39;All&#39;</li></ul><h2 id="_2-20-0-due-dates" tabindex="-1">2.20.0 - Due Dates <a class="header-anchor" href="#_2-20-0-due-dates" aria-label="Permalink to &quot;2.20.0 - Due Dates&quot;">​</a></h2><p><strong>Aug 7, 2023</strong></p><ul><li>Add ability to set task due dates in workflow definitions</li><li>Add abiltiy to edit task due date via mutations and task PATCH api</li><li>Filter tasks page in backend to allow viewing more tasks</li></ul><h2 id="_2-19-1-database-migrations" tabindex="-1">2.19.1 - Database Migrations <a class="header-anchor" href="#_2-19-1-database-migrations" aria-label="Permalink to &quot;2.19.1 - Database Migrations&quot;">​</a></h2><p><strong>Jul 27, 2023</strong></p><ul><li>Migrate tasks from specific workflow to fix bug</li></ul><h2 id="_2-19-0-airevault-integration" tabindex="-1">2.19.0 - AireVault Integration <a class="header-anchor" href="#_2-19-0-airevault-integration" aria-label="Permalink to &quot;2.19.0 - AireVault Integration&quot;">​</a></h2><p><strong>July 18, 2023</strong></p><ul><li>AireVault now integrated within AireFlow</li><li>Event log and workflow log filtering now occurs in the back end</li><li>Fix a bug where the same workflow key couldn&#39;t be used by multiple tenants</li><li>Added the task key column on both sets of logs</li><li>Ensure the custom event triggers drop down is now in alphabetical order</li><li>Fix a bug where events were sometimes not being marked as processed</li><li>Fix a bug where emitted contexts were not being used by created tasks for git-backed workflows after syncing</li><li>Created tasks are now prevented from being changed if the corresponding workflow has been deleted</li></ul><h2 id="_2-18-1-bug-fix" tabindex="-1">2.18.1 - Bug Fix <a class="header-anchor" href="#_2-18-1-bug-fix" aria-label="Permalink to &quot;2.18.1 - Bug Fix&quot;">​</a></h2><p><strong>Jun 14, 2023</strong></p><ul><li>Fix emitted context for workflows from git</li></ul><h2 id="_2-18-0-runtime-audit-workflow-keys" tabindex="-1">2.18.0 - Runtime Audit &amp; Workflow Keys <a class="header-anchor" href="#_2-18-0-runtime-audit-workflow-keys" aria-label="Permalink to &quot;2.18.0 - Runtime Audit &amp; Workflow Keys&quot;">​</a></h2><p><strong>Mar 30, 2023</strong></p><ul><li>Add audit logs to Elastic for event and task APIs</li><li>Add workflow definition keys</li><li>Prevent creation of filtered tasks</li><li>Fix HTTP task failure bug</li><li>Add custom headers to HTTP tasks</li></ul><h2 id="_2-17-1-rbac" tabindex="-1">2.17.1 - RBAC <a class="header-anchor" href="#_2-17-1-rbac" aria-label="Permalink to &quot;2.17.1 - RBAC&quot;">​</a></h2><p><strong>Feb 02, 2023</strong></p><ul><li>Add role based access control for client credentials</li><li>Add names to API keys</li><li>Add healthcheck endpoints</li><li>Fix event update issue</li></ul><h2 id="_2-17-0-rbac" tabindex="-1">2.17.0 - RBAC <a class="header-anchor" href="#_2-17-0-rbac" aria-label="Permalink to &quot;2.17.0 - RBAC&quot;">​</a></h2><p><strong>Dec 01, 2022</strong></p><ul><li>Add role based access control for users</li><li>Add role based access control for API keys</li><li>Add API for managing tenants</li></ul><h2 id="_2-16-0-task-keys-git-repository-retriever" tabindex="-1">2.16.0 - Task Keys &amp; Git Repository Retriever <a class="header-anchor" href="#_2-16-0-task-keys-git-repository-retriever" aria-label="Permalink to &quot;2.16.0 - Task Keys &amp; Git Repository Retriever&quot;">​</a></h2><p><strong>Nov 24, 2022</strong></p><ul><li>Add task key property and</li><li>Migrate triggers to use task key instead of ID</li><li>Add git repository retriever</li><li>Bug fixes for relationship diagram</li></ul><h2 id="_2-15-1-rabbitmq-bug-fixes" tabindex="-1">2.15.1 - RabbitMQ Bug Fixes <a class="header-anchor" href="#_2-15-1-rabbitmq-bug-fixes" aria-label="Permalink to &quot;2.15.1 - RabbitMQ Bug Fixes&quot;">​</a></h2><p><strong>Oct 10, 2022</strong></p><ul><li>Bug fixes for RabbitMQ</li><li>Bug fixes for relationship diagram</li><li>Task definition editor improvements</li></ul><h2 id="_2-15-0-aireframe-task-type" tabindex="-1">2.15.0 - AireFrame Task Type <a class="header-anchor" href="#_2-15-0-aireframe-task-type" aria-label="Permalink to &quot;2.15.0 - AireFrame Task Type&quot;">​</a></h2><p><strong>Sep 21, 2022</strong></p><ul><li>Add AireFrame form creation task type</li><li>Bug fixes for RabbitMQ</li><li>Automatically delete event context after processing</li><li>Automatically delete task contexts after completion/cancellation</li></ul><h2 id="_2-14-0-rabbitmq" tabindex="-1">2.14.0 - RabbitMQ <a class="header-anchor" href="#_2-14-0-rabbitmq" aria-label="Permalink to &quot;2.14.0 - RabbitMQ&quot;">​</a></h2><p><strong>July 05, 2022</strong></p><ul><li>Switch to RabbitMQ for event processing</li></ul><h2 id="_2-13-2-bug-fixes" tabindex="-1">2.13.2 - Bug Fixes <a class="header-anchor" href="#_2-13-2-bug-fixes" aria-label="Permalink to &quot;2.13.2 - Bug Fixes&quot;">​</a></h2><p><strong>June 16, 2022</strong></p><ul><li>Allow changing task descriptions via mutation or API patch call</li><li>Fix missing routing key label for custom event triggers</li><li>Add workflow logs for failed mutations</li></ul><h2 id="_2-13-1-bug-fixes" tabindex="-1">2.13.1 - Bug Fixes <a class="header-anchor" href="#_2-13-1-bug-fixes" aria-label="Permalink to &quot;2.13.1 - Bug Fixes&quot;">​</a></h2><p><strong>June 8, 2022</strong></p><ul><li>Fix bug for saving subject IDs and exmaple contexts for defintion</li><li>Fix downstream consequences warning when disabling a version</li></ul><h2 id="_2-13-0-versioning" tabindex="-1">2.13.0 - Versioning <a class="header-anchor" href="#_2-13-0-versioning" aria-label="Permalink to &quot;2.13.0 - Versioning&quot;">​</a></h2><p><strong>May 18, 2022</strong></p><ul><li>Add support for workflow definition versioning</li></ul><h2 id="_2-12-0-auth-fixes" tabindex="-1">2.12.0 - Auth Fixes <a class="header-anchor" href="#_2-12-0-auth-fixes" aria-label="Permalink to &quot;2.12.0 - Auth Fixes&quot;">​</a></h2><p><strong>Apr 21, 2022</strong></p><ul><li>Fix bug where UI gets stuck on &quot;Please authenticate&quot;</li></ul><h2 id="_2-11-0-general-improvements" tabindex="-1">2.11.0 - General Improvements <a class="header-anchor" href="#_2-11-0-general-improvements" aria-label="Permalink to &quot;2.11.0 - General Improvements&quot;">​</a></h2><p><strong>Apr 11, 2022</strong></p><ul><li>Improve pagination of workflow logs</li><li>Fix emitted context bug</li></ul><h2 id="_2-10-0-general-improvements" tabindex="-1">2.10.0 - General Improvements <a class="header-anchor" href="#_2-10-0-general-improvements" aria-label="Permalink to &quot;2.10.0 - General Improvements&quot;">​</a></h2><p><strong>Mar 24, 2022</strong></p><ul><li>Add task status description API</li><li>Add HTML email editor</li><li>Add toggle for HTTP/email failure retrying</li><li>Remove v1 of GraphQL API</li><li>Remove option for in memory repositories</li></ul><h2 id="_2-9-0-general-improvements" tabindex="-1">2.9.0 - General Improvements <a class="header-anchor" href="#_2-9-0-general-improvements" aria-label="Permalink to &quot;2.9.0 - General Improvements&quot;">​</a></h2><p><strong>Mar 10, 2022</strong></p><ul><li>Add total number of tasks to graphQL</li><li>Improve default job engine retry policy</li><li>Display tenant/user name in top bar</li><li>Automatically set task status to &quot;failed&quot; when an error occurs</li><li>(FIX) GraphQL sends Task statuses in a different format to REST APIs</li><li>(FIX) Long subject IDs do not display correctly in Event Log</li><li>(FIX) Long subject IDs do not display correctly in task list</li><li>(FIX) Tasks can reference themselves</li><li>(FIX) Searching should ignore white space at end of search term</li><li>(FIX) When a JWT expires it doesn&#39;t refresh and there&#39;s no way to get back into the staging/prod environments</li></ul><h2 id="_2-8-1-graphql-api-explorer-fix" tabindex="-1">2.8.1 - GraphQL API Explorer Fix <a class="header-anchor" href="#_2-8-1-graphql-api-explorer-fix" aria-label="Permalink to &quot;2.8.1 - GraphQL API Explorer Fix&quot;">​</a></h2><p><strong>Feb 15, 2022</strong></p><ul><li>Fix GraphQL Explorer for v2 of the API</li></ul><h2 id="_2-8-0-graphql-v2" tabindex="-1">2.8.0 - GraphQL V2 <a class="header-anchor" href="#_2-8-0-graphql-v2" aria-label="Permalink to &quot;2.8.0 - GraphQL V2&quot;">​</a></h2><p><strong>Feb 10, 2022</strong></p><ul><li>Add new version of GraphQL with performance improvements</li><li>Add workflow logs for failed emails and http requests</li></ul><h2 id="_2-7-0-task-cancellation-workflow-logs" tabindex="-1">2.7.0 - Task Cancellation &amp; Workflow Logs <a class="header-anchor" href="#_2-7-0-task-cancellation-workflow-logs" aria-label="Permalink to &quot;2.7.0 - Task Cancellation &amp; Workflow Logs&quot;">​</a></h2><p><strong>Jan 27, 2022</strong></p><ul><li>Add workflow logs screen to display errors within workflows</li><li>Add new task statuses (Cancelled, Failed &amp; Blocked)</li><li>Add validation when changing task status</li><li>Add API endpoints to fetch valid statuses and transitions</li><li>Minor UI improvements</li></ul><h2 id="_2-6-1-minor-graphql-fixes" tabindex="-1">2.6.1 - Minor GraphQL Fixes <a class="header-anchor" href="#_2-6-1-minor-graphql-fixes" aria-label="Permalink to &quot;2.6.1 - Minor GraphQL Fixes&quot;">​</a></h2><p><strong>Jan 14, 2022</strong></p><ul><li>Marginal improvement to GraphQL api performance</li></ul><h2 id="_2-6-0-definition-import-ui" tabindex="-1">2.6.0 - Definition Import UI <a class="header-anchor" href="#_2-6-0-definition-import-ui" aria-label="Permalink to &quot;2.6.0 - Definition Import UI&quot;">​</a></h2><p><strong>Dec 22, 2021</strong></p><ul><li>Add UI for importing workflow definitions</li><li>Fix GraphQL API Explorer authentication bug</li><li>Improve consistency of API scopes</li><li>Fix login bugs</li><li>Fix loading spinners</li><li>Add link to doc site in menu</li></ul><h2 id="_2-5-0-automatic-logout-redirect" tabindex="-1">2.5.0 - Automatic Logout Redirect <a class="header-anchor" href="#_2-5-0-automatic-logout-redirect" aria-label="Permalink to &quot;2.5.0 - Automatic Logout Redirect&quot;">​</a></h2><p><strong>Dec 06, 2021</strong></p><ul><li>Redirect back to login screen after logging out</li></ul><h2 id="_2-4-0-definition-import-export" tabindex="-1">2.4.0 - Definition Import &amp; Export <a class="header-anchor" href="#_2-4-0-definition-import-export" aria-label="Permalink to &quot;2.4.0 - Definition Import &amp; Export&quot;">​</a></h2><p><strong>Dec 01, 2021</strong></p><ul><li>The export button is now visible in the UI</li><li>New API added to import workflows</li><li>Allow OIDC and API keys to be used simultaneously</li></ul><h2 id="_2-3-1-display-event-contexts" tabindex="-1">2.3.1 - Display Event Contexts <a class="header-anchor" href="#_2-3-1-display-event-contexts" aria-label="Permalink to &quot;2.3.1 - Display Event Contexts&quot;">​</a></h2><p><strong>Nov 03, 2021</strong></p><ul><li>Event contexts are now visible in the event log</li></ul><h2 id="_2-3-0-merge-task-types" tabindex="-1">2.3.0 - Merge task types <a class="header-anchor" href="#_2-3-0-merge-task-types" aria-label="Permalink to &quot;2.3.0 - Merge task types&quot;">​</a></h2><p><strong>Sep 23, 2021</strong></p><ul><li>Task types now includes notification types (email and http) to simplify process of configuring a task</li></ul><h2 id="_2-2-0-triggered-updates-relationship-diagram" tabindex="-1">2.2.0 - Triggered Updates &amp; Relationship Diagram <a class="header-anchor" href="#_2-2-0-triggered-updates-relationship-diagram" aria-label="Permalink to &quot;2.2.0 - Triggered Updates &amp; Relationship Diagram&quot;">​</a></h2><p><strong>Sep 8, 2021</strong></p><ul><li>Add the ability to trigger task updates when events happen</li><li>Improve relationship diagram design</li></ul><h2 id="_2-1-0-event-definition-debug-correlation-id" tabindex="-1">2.1.0 - Event Definition Debug &amp; Correlation ID <a class="header-anchor" href="#_2-1-0-event-definition-debug-correlation-id" aria-label="Permalink to &quot;2.1.0 - Event Definition Debug &amp; Correlation ID&quot;">​</a></h2><p><strong>Aug 6, 2021</strong></p><ul><li>Add a button to event definitions to test fire an event</li><li>Add (optional) correlation IDs to tasks</li><li>Fix tasks UI bugs</li><li>Improve event log</li></ul><h2 id="_2-0-8-migrations" tabindex="-1">2.0.8 - Migrations <a class="header-anchor" href="#_2-0-8-migrations" aria-label="Permalink to &quot;2.0.8 - Migrations&quot;">​</a></h2><p><strong>Jul 14, 2021</strong></p><ul><li>Automatic data migrations</li></ul><h2 id="_2-0-7-bug-fixes" tabindex="-1">2.0.7 - Bug Fixes <a class="header-anchor" href="#_2-0-7-bug-fixes" aria-label="Permalink to &quot;2.0.7 - Bug Fixes&quot;">​</a></h2><p><strong>Jul 13, 2021</strong></p><ul><li>Fix task assignee editing bugs</li><li>Add subjectId to root level of liquid mapping</li><li>Remove filtered tasks from task list ui</li></ul><h2 id="_2-0-6-trigger-improvements" tabindex="-1">2.0.6 - Trigger Improvements <a class="header-anchor" href="#_2-0-6-trigger-improvements" aria-label="Permalink to &quot;2.0.6 - Trigger Improvements&quot;">​</a></h2><p><strong>Jul 9, 2021</strong></p><ul><li>Convert all triggers to use routing key internally</li><li>Fix triggering bugs</li><li>Use GraphQL for tasks list</li></ul><h2 id="_2-0-5-bug-fixes" tabindex="-1">2.0.5 - Bug Fixes <a class="header-anchor" href="#_2-0-5-bug-fixes" aria-label="Permalink to &quot;2.0.5 - Bug Fixes&quot;">​</a></h2><p><strong>Jun 25, 2021</strong></p><ul><li>Fix migration bug</li><li>Fix event log refresh bug</li><li>Fix error swallowing bug</li></ul><h2 id="_2-0-4-bug-fixes" tabindex="-1">2.0.4 - Bug Fixes <a class="header-anchor" href="#_2-0-4-bug-fixes" aria-label="Permalink to &quot;2.0.4 - Bug Fixes&quot;">​</a></h2><p><strong>Jun 24, 2021</strong></p><ul><li>Fix task emitted contexts</li><li>Add loading spinner</li><li>Replace TaskNotificationSent event with TaskComplete</li></ul><h2 id="_2-0-3-publishing-to-dockerhub" tabindex="-1">2.0.3 - Publishing to DockerHub <a class="header-anchor" href="#_2-0-3-publishing-to-dockerhub" aria-label="Permalink to &quot;2.0.3 - Publishing to DockerHub&quot;">​</a></h2><p><strong>Jun 14, 2021</strong></p><ul><li>Publish images on DockerHub</li></ul><h2 id="_2-0-2-ui-bug-fixes" tabindex="-1">2.0.2 - UI Bug Fixes <a class="header-anchor" href="#_2-0-2-ui-bug-fixes" aria-label="Permalink to &quot;2.0.2 - UI Bug Fixes&quot;">​</a></h2><p><strong>May 27, 2021</strong></p><ul><li>Improve relationship diagram</li><li>Fix trigger context UI bug</li></ul><h2 id="_2-0-1-bug-fixes" tabindex="-1">2.0.1 - Bug Fixes <a class="header-anchor" href="#_2-0-1-bug-fixes" aria-label="Permalink to &quot;2.0.1 - Bug Fixes&quot;">​</a></h2><p><strong>May 26, 2021</strong></p><ul><li>Fix a bug around invalid xpath expressions in filters</li><li>Improve management screen labels for tasks and triggers</li></ul><h2 id="_2-0-0-event-architecture" tabindex="-1">2.0.0 - Event Architecture <a class="header-anchor" href="#_2-0-0-event-architecture" aria-label="Permalink to &quot;2.0.0 - Event Architecture&quot;">​</a></h2><p><strong>May 20, 2021</strong></p><ul><li>Combine APIs onto same domain as website to remove CORS</li><li>Replace workflow instances with task instances</li><li>Replace workflow streams with custom event definitions</li><li>Replace &quot;once&quot; triggers with custom event triggers</li><li>Posting a custom event is now the only way to instantiate a workflow</li><li>Move triggers from workflows into individual tasks</li><li>Create a distinction between &quot;manual&quot; tasks and &quot;notification&quot; tasks</li><li>Disable versioning</li><li>Disable scheduled triggers</li></ul><h2 id="_1-10-0-bug-fixes" tabindex="-1">1.10.0 - Bug fixes <a class="header-anchor" href="#_1-10-0-bug-fixes" aria-label="Permalink to &quot;1.10.0 - Bug fixes&quot;">​</a></h2><p><strong>Apr 20, 2021</strong></p><ul><li>Add validation when saving workflow definitions</li><li>Improve error handling in definitions list</li><li>Remove need for double quotes in Output Context</li></ul><h2 id="_1-9-1-email-body-bug-fixes" tabindex="-1">1.9.1 - Email Body Bug Fixes <a class="header-anchor" href="#_1-9-1-email-body-bug-fixes" aria-label="Permalink to &quot;1.9.1 - Email Body Bug Fixes&quot;">​</a></h2><p><strong>Apr 13, 2021</strong></p><ul><li>Fix a bug causing links inside email bodies to break</li></ul><h2 id="_1-9-0-graphql-validation-security-bug-fixes" tabindex="-1">1.9.0 - GraphQL, Validation &amp; Security/Bug fixes <a class="header-anchor" href="#_1-9-0-graphql-validation-security-bug-fixes" aria-label="Permalink to &quot;1.9.0 - GraphQL, Validation &amp; Security/Bug fixes&quot;">​</a></h2><p><strong>Apr 9, 2021</strong></p><ul><li>GraphQL API for tasks</li><li>Frontend workflow validation hints</li><li>Support for literal values containing quotes</li><li>Prevent xpath/liquid injection</li><li>Bug fixes</li></ul><h2 id="_1-8-0-task-status-filtering" tabindex="-1">1.8.0 - Task Status Filtering <a class="header-anchor" href="#_1-8-0-task-status-filtering" aria-label="Permalink to &quot;1.8.0 - Task Status Filtering&quot;">​</a></h2><p><strong>Mar 25, 2021</strong></p><ul><li>Allow tasks to be filtered by status</li></ul><h2 id="_1-7-2-management-screen-bug-fixes" tabindex="-1">1.7.2 - Management Screen Bug Fixes <a class="header-anchor" href="#_1-7-2-management-screen-bug-fixes" aria-label="Permalink to &quot;1.7.2 - Management Screen Bug Fixes&quot;">​</a></h2><p><strong>Mar 23, 2021</strong></p><ul><li>Fix a bug causing workflow detail sections to collapse on first edit</li><li>Enabled deletion of task initial state pairs</li></ul><h2 id="_1-7-1-task-update-bug-fixes" tabindex="-1">1.7.1 - Task Update Bug Fixes <a class="header-anchor" href="#_1-7-1-task-update-bug-fixes" aria-label="Permalink to &quot;1.7.1 - Task Update Bug Fixes&quot;">​</a></h2><p><strong>Mar 17, 2021</strong></p><ul><li>Fix a bug causing task updates to re-run workflows</li></ul><h2 id="_1-7-0-event-bug-fixes" tabindex="-1">1.7.0 - Event Bug Fixes <a class="header-anchor" href="#_1-7-0-event-bug-fixes" aria-label="Permalink to &quot;1.7.0 - Event Bug Fixes&quot;">​</a></h2><p><strong>Mar 16, 2021</strong></p><ul><li>Fix a bug causing events to be fired multiple times</li></ul><h2 id="_1-5-0-task-filtering" tabindex="-1">1.5.0 - Task Filtering <a class="header-anchor" href="#_1-5-0-task-filtering" aria-label="Permalink to &quot;1.5.0 - Task Filtering&quot;">​</a></h2><p><strong>Mar 15, 2021</strong></p><ul><li>New subject/assignee filters in the task list</li><li>Fix task status button overflow</li><li>Reduce page exit popups</li></ul><h2 id="_1-4-1-event-hooks-endpoint-changes" tabindex="-1">1.4.1 - Event Hooks &amp; Endpoint Changes <a class="header-anchor" href="#_1-4-1-event-hooks-endpoint-changes" aria-label="Permalink to &quot;1.4.1 - Event Hooks &amp; Endpoint Changes&quot;">​</a></h2><p><strong>Mar 12, 2021</strong></p><ul><li>New Patch Api in Tasks Controller</li><li>Removed existing UpdateStatus endpoint</li><li>Removed existing UpdateState endpoint</li><li>Implement event hooks (TaskAssigned)</li></ul><h2 id="_1-3-0-event-hooks-bugs" tabindex="-1">1.3.0 - Event Hooks &amp; Bugs <a class="header-anchor" href="#_1-3-0-event-hooks-bugs" aria-label="Permalink to &quot;1.3.0 - Event Hooks &amp; Bugs&quot;">​</a></h2><p><strong>Mar 03, 2021</strong></p><ul><li>Implement event hooks (TaskInstantiated, TaskNotificationSent, TaskStarted, TaskCompleted, WorkflowCompleted)</li><li>Fix get tasks by subject</li></ul><h2 id="_1-2-0-ui-consistency" tabindex="-1">1.2.0 - UI Consistency <a class="header-anchor" href="#_1-2-0-ui-consistency" aria-label="Permalink to &quot;1.2.0 - UI Consistency&quot;">​</a></h2><p><strong>Feb 24, 2021</strong></p><ul><li>Task UI re-styling for consistency with management UI</li><li>Event trigger versioning UI bugfix</li></ul><h2 id="_1-1-0-management-ui" tabindex="-1">1.1.0 - Management UI <a class="header-anchor" href="#_1-1-0-management-ui" aria-label="Permalink to &quot;1.1.0 - Management UI&quot;">​</a></h2><p><strong>Jan 08, 2021</strong></p><ul><li>Refreshed management UI</li><li>AireIdentity integration bugfix</li></ul><h2 id="_1-0-0-initial-release" tabindex="-1">1.0.0 - Initial release <a class="header-anchor" href="#_1-0-0-initial-release" aria-label="Permalink to &quot;1.0.0 - Initial release&quot;">​</a></h2><p><strong>Jan 08, 2021</strong></p><ul><li>Initial release</li><li>AireIdentity integration</li></ul>',241),o=[l];function n(s,u,d,g,h,p){return a(),i("div",null,o)}const f=e(r,[["render",n]]);export{m as __pageData,f as default};
